import React, { useState } from 'react'
import Header from '../components/Header.js'
const Addservice = () => {

    const Upload = () => {
        const [highlight, setHighlight] = React.useState(false);
        const [preview, setPreview] = React.useState("");
        const [drop, setDrop] = React.useState(false);
      
        const handleEnter = (e) => {
          e.preventDefault();
          e.stopPropagation();
          console.log("enter!");
      
          preview === "" && setHighlight(true);
        };
      
        const handleOver = (e) => {
          e.preventDefault();
          e.stopPropagation();
          console.log("over!");
      
          preview === "" && setHighlight(true);
        };
      
        const handleLeave = (e) => {
          e.preventDefault();
          e.stopPropagation();
          console.log("leave!");
          setHighlight(false);
        };
      
        const handleUpload = (e) => {
          e.preventDefault();
          e.stopPropagation();
          console.log("drop!");
          setHighlight(false);
          setDrop(true);
      
          const [file] = e.target.files || e.dataTransfer.files;
      
          uploadFile(file);
        };
      
        function uploadFile(file) {
          const reader = new FileReader();
          reader.readAsBinaryString(file);
      
          reader.onload = () => {
            // this is the base64 data
            const fileRes = btoa(reader.result);
            console.log(`data:image/jpg;base64,${fileRes}`);
            setPreview(`data:image/jpg;base64,${fileRes}`);
          };
      
          reader.onerror = () => {
            console.log("There is a problem while uploading...");
          };
        }
      
        return (
          <>
            <div
              onDragEnter={(e) => handleEnter(e)}
              onDragLeave={(e) => handleLeave(e)}
              onDragOver={(e) => handleOver(e)}
              onDrop={(e) => handleUpload(e)}
              className={`upload${
                highlight ? " is-highlight" : drop ? " is-drop" : ""
              }`}
              style={{ backgroundImage: `url(${preview})` }}
            >
              <form className="my-form">
                <p>Drop image here</p>
                <div className="upload-button">
                  <input
                    type="file"
                    className="upload-file"
                    accept="image/*"
                    onChange={(e) => handleUpload(e)}
                  />
                  <button className="button">Upload Here</button>
                </div>
              </form>
            </div>
          </>
        );
      };
      
      ReactDOM.render(<Upload />, document.getElementById("root"));
      



    return (

        <>

            <Header />
            <div>
                <main id="main" className="main">
                    <div className="pagetitle">
                        <h1>Add Services</h1>
                        <nav>
                            <ol className="breadcrumb">
                                <li className="breadcrumb-item">Home</li>
                                <li className="breadcrumb-item active">Add Services</li>
                            </ol>
                        </nav>
                    </div>{/* End Page Title */}
                    <section className="section dashboard">
                        <div className="row">
                            {/* Left side columns */}
                            <div className="col-lg-8">
                                <div className="row">
                                    <div className="col-12">
                                        <div className="card recent-sales overflow-auto">
                                            <div className="card-body">
                                                <h5 className="card-title">Add Services <span>| Today</span></h5>
                                                <form>
                                                    <div className="row mb-3">
                                                        <label className="col-sm-2 col-form-label">Services Details</label>
                                                        <div className="col-sm-10">
                                                            <div className="form-floating mb-3">
                                                                <input type="text" className="form-control" id="floatingInput" placeholder="Services Name" />
                                                                <label htmlFor="floatingInput">Services Name</label>
                                                            </div>
                                                            <div className="form-floating mb-3">
                                                                <textarea className="form-control" placeholder="Leave a comment here" id="floatingTextarea" style={{ height: 100 }} defaultValue={""} />
                                                                <label htmlFor="floatingTextarea">Services Details</label>
                                                            </div>
                                                        </div>
                                                    </div>
                                                    <div className="row mb-3">
                                                        <label className="col-sm-2 col-form-label">Services Images</label>
                                                        <div id="root"></div>
                                                    </div>
                                                    <div className="row mb-5">
                                                        <label className="col-sm-2 col-form-label">Provider Details</label>
                                                        <div className="col-sm-10">
                                                            <div className="input-group mb-3">
                                                                <span className="input-group-text" id="basic-addon1">@</span>
                                                                <input type="email" className="form-control" placeholder="Provider Email" aria-label="Provideremail" aria-describedby="basic-addon1" />
                                                            </div>
                                                        </div>
                                                    </div>
                                                </form>{/* End General Form Elements */}

                                            </div>
                                        </div>
                                    </div>
                                </div>
                            </div>
                        </div>
                    </section>
                </main>{/* End #main */}
                {/* ======= Footer ======= */}
                <footer id="footer" className="footer">
                    <div className="copyright">
                        © Copyright <strong><span>Sky Digital</span></strong>. All Rights Reserved
                    </div>
                    <div className="credits">
                        Designed by <a href="https://skydigitalgrapgics.in/">Dharmik Manguliya</a>
                    </div>
                </footer>{/* End Footer */}
                <a href="#" className="back-to-top d-flex align-items-center justify-content-center"><i className="bi bi-arrow-up-short" /></a>
            </div>

        </>
    )
}


export default Addservice
