import React, { useState } from 'react'
import Header from '../components/Header.js'
const Addservice = () => {


    <>
        function dragOver(e) {'{'}
        e.stopPropagation();
        e.preventDefault();
        if (e.type == "dragover") {'{'}
        $(e.target).css({'{'}
        "background-image": "black",
        "outline-offset": "-20px"
        {'}'});
        {'}'} else {'{'}
        $(e.target).css({'{'}
        "background-color": "gray",
        "outline-offset": "-10px"
        {'}'});
        {'}'}
        {'}'}
        function uploadFiles(e) {'{'}
        e.stopPropagation();
        e.preventDefault();
        dragOver(e); //1
        // undefined로 뜰 때가 있는데 (아마 jQuery를 사용할 때) 명시적으로 재정의해주면 된다. 
        e.dataTransfer = e.originalEvent.dataTransfer; //2
        var files = e.target.files || e.dataTransfer.files;
        // console.log( JSON.stringify( files, null, '    ' ) );
        // console.log( files[ 0 ] );
        if (files.length &gt; 1) {'{'}
        alert('하나만 올려라.');
        return;
        {'}'}
        if (files[0].type.match(/image.*/)) {'{'}
        $(e.target).css({'{'}
        "background-image": "url(" + window.URL.createObjectURL(files[0]) + ")",
        "outline": "none",
        "background-size": "100% 100%",
        {'}'});
        {'}'}else{'{'}
        alert('이미지가 아닙니다.');
        return;
        {'}'}
        {'}'}
        $( document ).ready(function() {'{'}
        $('.content')
        .on("dragover", dragOver)
        .on("dragleave", dragOver)
        .on("drop", uploadFiles);
        $('.target').click(function() {'{'}
        console.log( "click" );
        // $("#uploadFiles").click();
        document.getElementById('uploadFiles').click()
        {'}'})
        {'}'});

    </>
    return (
        <>
            <Header />
            <div class="w-100 d-flex container py-5 align-items-center">
                <div class="w-50 px-4">
                    <h3 class="text-danger ">+ Add Service</h3>
                    <form class="pt-3" enctype="multipart/form-data">
                        <div class="mb-3">
                            <label for="exampleInputEmail1" class="form-label fw-bold fs-5 pb-1">Service name</label>
                            <input type="text" class="form-control shadow p-3 rounded-pill" name="addservices" />
                        </div>
                        <div class="mb-3">
                            <label for="exampleInputEmail1" class="form-label fw-bold fs-5 pb-1">Service Details</label>
                            <textarea class="form-control shadow p-3 rounded-5" name="servicedetails" id="" cols="20" rows="5" />
                        </div>
                        <div class="mb-3">
                            <label for="exampleInputEmail1" class="form-label fw-bold fs-5 pb-1">Provider Email id</label>
                            <input type="text" class="form-control shadow p-3 rounded-pill" name="provideremail" />
                        </div>
                        <div>
                            <button class="rounded-pill px-5 py-3 bg-danger border-0 text-white fw-bold mt-3" type="submit">Submit</button>
                        </div>
                    </form>
                </div>
                <div class="w-50 px-5">
                    <section>
                        <div class="content"></div>
                        <input type="file" class="custom-upload__input" name="serviceimage" id="uploadFile" multiple="multiple" />
                    </section>
                    <section class="upload" />
                </div>
            </div>
        </>
    )
}


export default Addservice
