import React, { useState } from "react";

const Home = () => {
  const [file, setFile] = useState(null);

  const setImageAction = async (event) => {
    event.preventDefault();

    if (!file) {
      console.log("Please select a file, enter a tower name, and add at least one wing");
      return;
    }

    const formData = new FormData();
    formData.append("image", file);
    const token = localStorage.getItem("token");

    try {
      const response = await fetch("http://localhost:8000/admin/addservices",{
        method: "POST",
        body: formData,
        headers:{token}
      })

      if (response.ok) {
        console.log("Successfully uploaded image");
        // You can do something after a successful upload, e.g., redirect or display a success message.
      } else {
        console.log("Error uploading image");
        // Handle the error, e.g., show an error message to the user.
      }
    } catch (error) {
      console.error("An error occurred:", error);
    }
  }

  const handleFileChange = (event) => {
    setFile(event.target.files[0]);
  };



  return (
    <div className="content">
      <form onSubmit={setImageAction}>
        <input type="file" name="image" onChange={handleFileChange} />
        <br />
        <br />       
        <button type="submit" name="upload">
          Upload
        </button>
      </form>
    </div>
  )
}

export default Home
