import React, { useState } from "react";

const Home = () => {
  const [files, setFiles] = useState([]);

  const setImageAction = async (event) => {
    event.preventDefault();

    if (files.length === 0) {
      console.log("Please select at least one file");
      return;
    }

    const formData = new FormData();

    // Append all selected files to the FormData object
    for (const file of files) {

      formData.append("image", file);
    
    }

    try {
      const response = await fetch("http://192.168.1.111:8000/admin/addservices", {
        method: "POST",
        body: formData,
      });

      if (response.ok) {
        console.log("Successfully uploaded images");
        // You can do something after a successful upload, e.g., redirect or display a success message.
      } else {
        console.log("Error uploading images");
        // Handle the error, e.g., show an error message to the user.
      }
    } catch (error) {
      console.error("An error occurred:", error);
    }
  };

  const handleFileChange = (event) => {
    const selectedFiles = Array.from(event.target.files);
    setFiles(selectedFiles);
  };

  return (
    <div className="content">
      <form onSubmit={setImageAction}>
        <input type="file" name="images" onChange={handleFileChange} multiple />
        <br />
        <br />
        <button type="submit" name="upload">
          Upload
        </button>
      </form>
    </div>
  );
};

export default Home;
